mapper.select:
SELECT tor.*,tip.`PROJECTNAME` AS ITEMNAME ,tip.`HOMEIMG` AS ITEMLOGO,tp.`PRODUCTID`,tp.`PROTOCOLURL`,tbu.`USERNAME` AS sellerName,tbu.`ID` as sellerUserId FROM `T_ORDER_USER` tu LEFT JOIN `T_ORDER` tor ON tor.`ORDERID` = tu.`ORDERID` LEFT JOIN `T_ORDER_PRODUCT` tp ON tp.`ORDERID` = tor.`ORDERID` LEFT JOIN `T_INTELLECTUAL_PROJECT` tip ON tip.`ID` = tp.`PRODUCTID` LEFT JOIN `T_BHW_USER` tbu ON tbu.`ID` = tip.`USERID` WHERE tu.`USERID`=#{userOrderVo.userId}
and tor.STATUS = #{userOrderVo.status}
and (tor.`ORDERID` like concat('%',#{userOrderVo.orderVague},'%') or tor.`ORDERNUM` like concat('%',#{userOrderVo.orderVague},'%') or tor.`ITEMNAME` like concat('%',#{userOrderVo.orderVague},'%') )
order by tu.CREATETIME desc

SELECT * FROM ( SELECT tor.`ORDERID`,tor.`ORDERNUM`,tor.`TOTALPRICE`,tor.`PAYSTATUS`,tip.`PROJECTNAME` AS ITEMNAME , tip.`HOMEIMG` AS ITEMLOGO,tp.`PRODUCTID`,tp.`PROTOCOLURL`,tbu.`USERNAME` AS sellerName, tbuo.`USERNAME` AS buyerName,tor.`CREATETIME`,tor.UPDATETIME,0 AS dealType,trr.`STATUS` AS refundStatus FROM `T_ORDER_USER` tu LEFT JOIN `T_ORDER` tor ON tor.`ORDERID` = tu.`ORDERID` LEFT JOIN `T_ORDER_PRODUCT` tp ON tp.`ORDERID` = tor.`ORDERID` LEFT JOIN `T_INTELLECTUAL_PROJECT` tip ON tip.`ID` = tp.`PRODUCTID` LEFT JOIN `T_BHW_USER` tbu ON tbu.`ID` = tip.`USERID` LEFT JOIN `T_BHW_USER` tbuo ON tbuo.`ID` = tu.`USERID` LEFT JOIN T_REFUND_RECORD trr ON trr.`ORDERID` = tor.`ORDERID` WHERE tor.`PAYSTATUS`=2 AND tor.`STATUS`=2 UNION ALL SELECT t.`ORDERID`,t.`ORDERNUM`,t.`TOTALPRICE`,t.`PAYSTATUS`,'' AS ITEMNAME ,'' AS ITEMLOGO,'', '','官方' AS sellerName,tbuo.`USERNAME` AS buyerName, t.CREATETIME,t.UPDATETIME,t.dealType,0 FROM T_WALLET_ORDER t LEFT JOIN `T_BHW_USER` tbuo ON tbuo.`ID` = t.`USERID` WHERE t.`PAYSTATUS`=2 ) af where 1=1
and af.dealType = #{userOrderVo.dealType}
and af.`ORDERID` like concat('%',#{userOrderVo.orderId},'%')
and af.`ORDERNUM` like concat('%',#{userOrderVo.orderNum},'%')
and DATE_FORMAT( af.`CREATETIME`, '%Y-%m-%d' ) BETWEEN #{userOrderVo.createTime} AND #{userOrderVo.updateTime}
ORDER BY af.CREATETIME DESC

SELECT t.* FROM T_WALLET_ORDER t WHERE t.`USERID`=#{userOrderVo.userId}


    and t.`ORDERID` like  concat('%',#{userOrderVo.orderId},'%')


    and t.`ORDERNUM` like  concat('%',#{userOrderVo.orderNum},'%')



    and DATE_FORMAT( t.`CREATETIME`, '%Y-%m-%d' ) BETWEEN #{userOrderVo.createTime} AND #{userOrderVo.updateTime}

order by t.CREATETIME desc

mapper.update:
update T_ORDER set

    STATUS=#{userOrderVo.status}


    CLOSESTATUS=#{userOrderVo.payStatus}

where ORDERID=#{userOrderVo.orderId}

update T_ORDER_PRODUCT set

    PROTOCOLURL=#{userOrderVo.protocolUrl}

where ORDERID=#{userOrderVo.orderId} and PRODUCTID=#{userOrderVo.productId}
